    public static ArrayList<CarAccident> getCAccidents(){
        ArrayList<CarAccident> carAccidentsList = new ArrayList<>();

        for(int i =0; i < carData.length; i++){
            String currentLine = carData[i];
            String [] details = currentLine.split("\\s+");

            String date = details[0];
            String [] dateDetails = date.split("-");

            int year = Integer.parseInt(dateDetails[0]);
            int month = Integer.parseInt(dateDetails[1]);
            int day = Integer.parseInt(dateDetails[2]);


            String time = details[1];
            String h = time.substring(0,2); int hour = Integer.parseInt(h);
            String m = time.substring(3,5); int minutes = Integer.parseInt(m);
            String s = time.substring(6);  int seconds = Integer.parseInt(s);

            AccidentTime timeOfAccident = new AccidentTime(year,month,day,hour,minutes,seconds);

            double lat = Double.parseDouble(details[2]);
            double lon = Double.parseDouble(details[3]);
            double acceleration = Double.parseDouble(details[4]);

            CarAccident accident = new CarAccident(timeOfAccident, lat, lon, acceleration);
            carAccidentsList.add(accident);

        }
        return carAccidentsList;
    }

